package StrongholdCrusader.GameObjects.Humans;import StrongholdCrusader.Map.MapGUI;import StrongholdCrusader.ResourceManager;import StrongholdCrusader.Settings;import javafx.animation.Animation;import javafx.animation.KeyFrame;import javafx.animation.Timeline;import javafx.scene.control.CheckBox;import javafx.scene.control.ProgressBar;import javafx.scene.image.ImageView;import javafx.scene.layout.AnchorPane;import javafx.util.Duration;/** * Created by Baran on 5/29/2017. */public class Soldier extends Human {    public AnchorPane anchorPane;    private CheckBox checkBox;    public Soldier() {        this.type = "Soldier";        this.speed = Settings.SOLDIER_SPEED;        this.zone = Settings.SOLDIER_ATTACK_RADIUS;        this.power = Settings.SOLDIER_POWER;        this.health = Settings.SOLDIER_INITIAL_HEALTH;    }    public Soldier(MapGUI mapGUI) {        super(mapGUI);        setImage(ResourceManager.getImage("soldierIdleDown"));        this.type = "Soldier";        this.speed = Settings.SOLDIER_SPEED;        this.zone = Settings.SOLDIER_ATTACK_RADIUS;        this.power = Settings.SOLDIER_POWER;        this.health = Settings.SOLDIER_INITIAL_HEALTH;    }    public AnchorPane objectsMenuAnchorPane(boolean owner) {        initializeAnchorPane();        checkBox.setOnMouseClicked(event -> mapGUI.changeClimbStatus(Soldier.this, checkBox.isSelected()));        if (!owner) {            checkBox.setVisible(false);        }        return anchorPane;    }    @Override    public void initializeAnchorPane() {        anchorPane = new AnchorPane();        ImageView imageView = new ImageView(ResourceManager.getImage("Soldier"));        checkBox = new CheckBox("Can Climb ?");        checkBox.setLayoutX(400);        checkBox.setLayoutY(60);        checkBox.setSelected(canClimb);        imageView.setLayoutX(100);        imageView.setLayoutY(40);        ProgressBar healthBar = new ProgressBar((double) this.health / Settings.SOLDIER_INITIAL_HEALTH);        healthBar.setLayoutX(Settings.MENUS_ANCHOR_PANE_WIDTH - 150);        healthBar.setStyle("-fx-accent: #96ff4c;");        healthBar.setLayoutY(20);        healthBar.setPrefSize(100, 20);        anchorPane.getChildren().addAll(imageView, checkBox, healthBar);        anchorPane.setPrefHeight(100);        anchorPane.setPrefWidth(300);    }    @Override    public void changeAnimation() {        switch (direction) {            case DOWN: {                if (mode == humanMode.WALKING)                    createAnimation("soldierWalkingDown", 8);                if (mode == humanMode.ATTACKING)                    createAnimation("soldierAttackingDown", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("soldierIdleDown"));                }                break;            }            case UP: {                if (mode == humanMode.WALKING)                    createAnimation("soldierWalkingUp", 8);                if (mode == humanMode.ATTACKING)                    createAnimation("soldierAttackingUp", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("soldierIdleUp"));                }                break;            }            case RIGHT: {                if (mode == humanMode.WALKING)                    createAnimation("soldierWalkingRight", 8);                if (mode == humanMode.ATTACKING)                    createAnimation("soldierAttackingRight", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("soldierIdleRight"));                }                break;            }            case LEFT: {                if (mode == humanMode.WALKING)                    createAnimation("soldierWalkingLeft", 8);                if (mode == humanMode.ATTACKING)                    createAnimation("soldierAttackingLeft", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("soldierIdleLeft"));                }                break;            }        }    }}