package StrongholdCrusader.GameObjects.Humans;import StrongholdCrusader.Map.MapGUI;import StrongholdCrusader.Settings;import javafx.event.EventHandler;import javafx.scene.control.Button;import javafx.scene.control.CheckBox;import javafx.scene.control.ProgressBar;import javafx.scene.image.ImageView;import javafx.scene.input.MouseEvent;import javafx.scene.layout.AnchorPane;import java.io.File;/** * Created by Baran on 5/29/2017. */public class Soldier extends Human {    public AnchorPane anchorPane;    public Soldier() {        this.type = "Soldier";        this.speed = 1;        this.zone = 7;        this.power = Settings.SOLDIER_POWER;        this.health = 1000;    }    public Soldier(MapGUI mapGUI) {        super(mapGUI);        this.type = "Soldier";        this.speed = 1;        this.power = Settings.SOLDIER_POWER;        this.zone = 7;        this.health = 1000;    }    File file;    ImageView imageView;    Button button;    CheckBox checkBox;    ProgressBar healthBar;    @Override    public AnchorPane clickAction(boolean owner) {        initializeAnchorPane();        checkBox.setOnMouseClicked(new EventHandler<MouseEvent>() {            @Override            public void handle(MouseEvent event) {                mapGUI.changeClimbStatus(Soldier.this, checkBox.isSelected());            }        });        if (!owner){            button.setVisible(false);            checkBox.setVisible(false);        }        return anchorPane;    }    @Override    public void initializeAnchorPane() {        anchorPane = new AnchorPane();        file = new File("Resources/images/Humans/Soldier.png");        imageView = new ImageView(file.toURI().toString());        button = new Button("Change to Vassal");        checkBox = new CheckBox("Can Climb ?");        checkBox.setLayoutX(400);        checkBox.setLayoutY(60);        checkBox.setSelected(canClimb);        imageView.setLayoutX(100);        imageView.setLayoutY(40);        button.setLayoutX(200);        button.setLayoutY(50);        healthBar = new ProgressBar((double)this.health/100);        healthBar.setLayoutX(Settings.MENUS_ANCHORPANE_WIDTH - 150);        healthBar.setStyle("-fx-accent: #96ff4c;");        healthBar.setLayoutY(20);        healthBar.setPrefSize(100, 20);        anchorPane.getChildren().addAll(imageView, button, checkBox, healthBar);        anchorPane.setPrefHeight(100);        anchorPane.setPrefWidth(300);    }}