package StrongholdCrusader.GameObjects.Humans;import StrongholdCrusader.Map.MapGUI;import StrongholdCrusader.ResourceManager;import StrongholdCrusader.Settings;import javafx.scene.control.CheckBox;import javafx.scene.control.ProgressBar;import javafx.scene.image.ImageView;import javafx.scene.layout.AnchorPane;/** * Created by Baran on 5/29/2017. */public class Vassal extends Human {    public AnchorPane anchorPane;    private CheckBox checkBox;    public Vassal() {        this.type = "Vassal";        this.speed = Settings.VASSAL_SPEED;        this.zone = Settings.VASSAL_ATTACK_RADIUS;        this.power = Settings.HUMAN_POWER;        this.health = Settings.VASSAL_INITIAL_HEALTH;    }    public Vassal(MapGUI mapGUI) {        super(mapGUI);        setImage(ResourceManager.getImage("vassalIdleDown"));        this.type = "Vassal";        this.speed = Settings.VASSAL_SPEED;        this.zone = Settings.VASSAL_ATTACK_RADIUS;        this.power = Settings.HUMAN_POWER;        this.health = Settings.VASSAL_INITIAL_HEALTH;    }    public AnchorPane objectsMenuAnchorPane(boolean owner) {        initializeAnchorPane();        checkBox.setOnAction(event -> mapGUI.changeClimbStatus(Vassal.this, checkBox.isSelected()));        if (!owner) {            checkBox.setVisible(false);        }        return anchorPane;    }    @Override    public void initializeAnchorPane() {        anchorPane = new AnchorPane();        ImageView imageView = new ImageView(ResourceManager.getImage("Vassal"));        checkBox = new CheckBox("Can Climb ?");        checkBox.setLayoutX(400);        checkBox.setLayoutY(60);        checkBox.setSelected(canClimb);        imageView.setLayoutX(200);        ProgressBar health = new ProgressBar((double) this.health / Settings.VASSAL_INITIAL_HEALTH);        health.setLayoutX(Settings.MENUS_ANCHOR_PANE_WIDTH - 150);        health.setStyle("-fx-accent: #96ff4c;");        health.setLayoutY(20);        health.setPrefSize(100, 20);        anchorPane.getChildren().addAll(imageView, checkBox, health);        imageView.setLayoutX(200);        imageView.setLayoutY(40);        anchorPane.setPrefSize(300, 100);    }    @Override    public void changeAnimation() {        switch (direction) {            case DOWN: {                if (mode == humanMode.WALKING)                    createAnimation("vassalWalkingDown", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("vassalIdleDown"));                }                break;            }            case UP: {                if (mode == humanMode.WALKING)                    createAnimation("vassalWalkingUp", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("vassalIdleUp"));                }                break;            }            case RIGHT: {                if (mode == humanMode.WALKING)                    createAnimation("vassalWalkingRight", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("vassalIdleRight"));                }                break;            }            case LEFT: {                if (mode == humanMode.WALKING)                    createAnimation("vassalWalkingLeft", 8);                if (mode == humanMode.IDLE) {                    animationTimeLine.stop();                    this.setImage(ResourceManager.getImage("vassalIdleLeft"));                }                break;            }        }    }}