package StrongholdCrusader.Map;import StrongholdCrusader.ClientPlayer;import StrongholdCrusader.GameObjects.GameObject;import StrongholdCrusader.Settings;import java.io.Serializable;import java.util.LinkedList;/** * Created by Baran on 5/29/2017. */public class Map implements Serializable {    MapTile[][] tiles;    public LinkedList<GameObject> objects;    MapManager mapManager;    MapGUI gui;    ClientPlayer clientPlayer;    public Map(ClientPlayer clientPlayer) {        mapManager = new MapManager();        tiles = mapManager.getMapById(1);        objects = new LinkedList<>();        gui = new MapGUI(this);        this.clientPlayer = clientPlayer;    }    public void showMapScreen() {        gui.showMap();        new Thread(gui).start();    }    public void sendGameEvent(int type, String message) {        clientPlayer.client.sendGameEvent(type, message);    }    public GameObject findGameObjectObjectById(int id) {        for (GameObject object : objects) {            if (object.id == id)                return object;        }        return null;    }}